$(function(){toggleRelativeSubjectWords();ajaxGetRelativeSubjects();$(".words-list").on("click","input:checkbox",function(){var b=$("#txtMainSearchType").val();var a=$("#hidSearchKeysHidden").val();if($(this).prop("checked")){b+=" OR "+$(this).val();$("#txtMainSearchType").val(b);a+=" OR "+$(this).val();$("#hidSearchKeysHidden").val(a)}else{$("#txtMainSearchType").val(b.replace(" OR "+$(this).val(),""));$("#hidSearchKeysHidden").val(a.replace(" OR "+$(this).val(),""))}})});function toggleRelativeSubjectWords(){$("#searchRelative>div.relative-op>a").on("click",function(){if($(this).hasClass("close")){$("#searchRelative").children(".relative-words").hide();$(this).removeClass("close").addClass("open")}else{$("#searchRelative").children(".relative-words").show();$(this).removeClass("open").addClass("close")}})}function ajaxGetRelativeSubjects(){var a=$("#txtMainSearchType").val();if(a!=null&&a!=""){$.post("/ajax/RelativeSubjectWordsHtml.ashx",{search:a.split(",")[0],"-t":+new Date},function(b){if(b.length===0){$("#searchRelative").hide();return}if($("#hifOutObjectType").val()=="Articles"){$("#searchRelative").show()}$(".words-list").html(b.content)})}};